$(
	function() {
		// DÃ©but des instructions
		
		//0. Gestion des boutons
	$("#filtre li").on(
		"click",
		function() {
			$(this).addClass("selected");
			$(this).siblings().removeClass("selected");

		}
	);
		//  Categories
		$("#project-list").addClass("cat-visible");
		
		$("#btn-cat-tous").on(
			"click",
			function(){
				$(".project").addClass("cat-visible")
			}
		);
		$("#btn-cat-photo").on(
			"click",
			function() {
				$(".project").removeClass("cat-visible");
				$(".project.cat-photo").addClass("cat-visible");
			}
		);
		$("#btn-cat-lieux").on(
			"click",
			function() {
				$(".project").removeClass("cat-visible");
				$(".project.cat-lieux").addClass("cat-visible");			
			}
		);
		$("#btn-cat-note").on(
			"click",
			function() {
				$(".project").removeClass("cat-visible");
				$(".project.cat-note").addClass("cat-visible");			
			}
		);
	}
);



